import React from "react";
// TODO: import useFormik from formik library
import { useFormik } from 'formik';
function App() {
  // TODO: add a const called formik assigned to useFormik()
const formik = useFormik({
     initialValues: {
       emailField: '',
       pswField: ''
     },
     onSubmit: values =>{
      console.log('form:',values);
    },
    validate: values =>{
      let errors = {};
      let inputAddress = values.emailField;
      if(!/^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,4}$/i.test(inputAddress)) {
        let emailError = 'Username should be an email';
        errors.emailField = <div> {emailError} </div>;
      }
      if(!values.pswField) {
        let pswError = 'Required';
        errors.pswField = <div> {pswError} </div>;
      }
      if((/^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,4}$/i.test(inputAddress)) && values.pswField) {
        let submitBtn = 'Login successful';
        errors.pswField = <div> {submitBtn} </div>;
      return errors;
      }
    }
   });
  return (
    <div>
      <p>
      <form onSubmit={formik.handleSubmit}>
        <div>Email:</div>
        <input type="text" name="emailField" onChange={formik.handleChange} value={formik.values.emailField}/>
        {formik.errors.emailField ? <div style={{color:'red'}}>{formik.errors.emailError}</div> : null}        
        <div>Password:</div>
        <input type="text" name="pswField" onChange={formik.handleChange} value={formik.values.pswField}/><br/>
        {formik.errors.pswField ? <div style={{color:'red'}}>{formik.errors.pswError}</div> : null}                
        <button type="submit" id="submitBtn">Submit</button>
        <div id="success" style={{color:'green'}}>{formik.errors.submitBtn}</div>
      </form>
      </p>
    </div>
  );
}

export default App;
