import React from "react";
// TODO: import useFormik from formik library
import { useFormik } from 'formik';
function App() {
  // TODO: add a const called formik assigned to useFormik()
const formik = useFormik({
     initialValues: {
       emailField: '',
       pwdField: ''
     },
     onSubmit: values =>{
      console.log('form:',values);
    },
    validate: values =>{
      let errors = {};
      let inputAddress = values.emailField;
      if(!/^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,4}$/i.test(inputAddress)) {
        let emailError = 'Username should be an email';
        errors.emailField = <div> {emailError} </div>;
      }
      if(!values.pwdField) {
        let pwdError = 'Required';
        errors.pwdField = <div> {pwdError} </div>;
      }
      if (/^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,4}$/i.test(inputAddress) && values.pwdField){
        let success = 'Login Successful';

      }
      return errors;
    }
   });
  return (
    <div>
      <p>
      <form onSubmit={formik.handleSubmit}>
        <div>Email:</div>
        <input type="text" name="emailField" onChange={formik.handleChange} value={formik.values.emailField}/>
        {formik.errors.emailField ? <div style={{color:'red'}}>{formik.errors.emailField}</div> : null}        
        <div>Password:</div>
        <input type="text" name="pwdField" onChange={formik.handleChange} value={formik.values.pwdField}/><br/>
        {formik.errors.pwdField ? <div style={{color:'red'}}>{formik.errors.pwdField}</div> : null}                
        <button type="submit" >Submit</button>
      </form>
      </p>
    </div>
  );
}

export default App;
